targets.expr:
avg by (namespace) (
  zeebe_health{camunda_region=~"$region", namespace=~"$namespace"}
)

avg by (pod) (
  avg_over_time(kube_pod_container_status_ready{camunda_region=~"$region", container!~"curator|curl", namespace=~"$namespace"}[15m])
)

topk(10, avg by (namespace) (
  kubelet_volume_stats_used_bytes{camunda_region=~"$region", namespace=~"$namespace", persistentvolumeclaim=~".*zeebe.*"}
  /
  kubelet_volume_stats_capacity_bytes{camunda_region=~"$region", namespace=~"$namespace", persistentvolumeclaim=~".*zeebe.*"}
))

avg by (namespace) (
  kubelet_volume_stats_used_bytes{camunda_region=~"$region", namespace=~"$namespace", persistentvolumeclaim=~".*zeebe.*"}
  /
  kubelet_volume_stats_capacity_bytes{camunda_region=~"$region", namespace=~"$namespace", persistentvolumeclaim=~".*zeebe.*"}
)

sum by(method) (rate(grpc_server_processing_duration_seconds_count{
    camunda_region=~"$region",
    method=~"$method",
    namespace=~"$namespace",
    }[$__rate_interval]))

histogram_quantile(0.95,
  sum by (le, method) (
    increase(grpc_server_processing_duration_seconds_bucket{
      camunda_region=~"$region",
      method=~"$method",
      method!~"ActivateJobs"
    }[$__rate_interval])
  )
)

histogram_quantile(0.95,
  sum by (le, method) (
    increase(grpc_server_processing_duration_seconds_bucket{
      camunda_region=~"$region",
      method=~"$method",
      namespace=~"$namespace",
    }[$__rate_interval])
  )
)

sum by(method) (
  rate(grpc_server_processing_duration_seconds_count{
    camunda_region=~"$region",
    code!="OK",
    method=~"$method",
    namespace=~"$namespace",
  }[$__rate_interval])
)
/
sum by(method) (
  rate(grpc_server_processing_duration_seconds_count{
    camunda_region=~"$region",
    method=~"$method",
    namespace=~"$namespace",
  }[$__rate_interval])
)

sum by(uri, method) (
  rate(http_server_requests_seconds_count{
    camunda_region=~"$region",
    container=~"$container",
    namespace=~"$namespace",
    uri=~"$uri",
    uri!~"^/actuator/health(/.*)?$|^/actuator/prometheus$"
  }[$__rate_interval])
)

